float scissors(vec3 p) {
    /* outer */
    p.z+=0.98;
    p.y+=0.12;  
    p.x-=0.95;
 p.xz=rot2(p.xz, -3.0 ); //rotate outer claw
    vec3 q=p;
    p.x+=-0.8+ sin(2.0+p.z*3.8)*0.8;
    p.y+= sin(p.y*2.5)*0.6;
    float o=length(p)-0.22;
    /* inner */
q.xz=rot2(q.xz, 0.0 );    // rotate inner claw
    q.x-=0.5+ sin(q.z*5.8)*0.44;
    q.z+=0.1;
    q.y+=sin(q.y*3.5)*0.6;
    float i=length(q)-0.18;
    return min(o,i);
}
/* chained leg...  
  r.z=rotation on z axis,
  and r.yzw rotate leg joints
*/
float leg(vec3 p, vec4 r) {
    p.x+=0.7-r.x;
    p.y+=0.05;
    p.z-=r.x;
p.xz=rot2(p.xz, r.x );    
p.xy=rot2(p.xy, -1.57+r.y );
    p.y-=0.2;
    float b=sdCylinder(p, vec2(0.03,0.2));
    p.y-=0.18;
p.xy=rot2(p.xy, -1.57+r.z ); 
    p.y-=0.13;
    float m=sdCylinder(p, vec2(0.03,0.13));
    p.y-=0.11;
p.xy=rot2(p.xy, -r.w ); 
    p.y-=0.1;
    float l=sdCylinder(p, vec2(0.02,0.1)) + sin(p.y)*0.1;
    return min(min(b,m),l);
}
/***********************************************/
vec2 opU(vec2 a, vec2 b) {
	return mix(a, b, step(b.x, a.x));
}
/***********************************************/
vec2 DE(vec3 p) {
    vec3 q=vec3(-p.x,p.y,p.z);


    float b=min(body(p),eyesticks(p)); 
    float c=min(clamps(p), clamps(q));
    float d=min( min(leg(p, vec4(0.0,0.1,0.2,0.3+0.4)), leg(p, vec4(0.15,0.1,0.2,0.3+0.4)) ), leg(p, vec4(0.3,0.1,0.2,0.3+0.4)));
    float g=min( min(leg(q, vec4(0.0,0.1,0.2,0.3+0.4)), leg(q, vec4(0.15,0.1,0.2,0.3+0.4)) ), leg(q, vec4(0.3,0.1,0.2,0.3+0.4)));
    vec2 m1=vec2(min(b,min(c,min(d,g))),1.0);
    vec2 m2=vec2(min( eyes(p), min(scissors(p),scissors(q)) ), 2.0);

    m1=opU(m1,m2);

    /* floor */
    m2=vec2( p.y+0.5-noise2(p.xz*2.8)*0.1, 3.0);

    return opU(m1,m2);
}
/***********************************************/
vec3 normal(vec3 p) {
	vec3 e=vec3(0.01,-0.01,0.0);
	return normalize( vec3(	e.xyy*DE(p+e.xyy).x +	e.yyx*DE(p+e.yyx).x +	e.yxy*DE(p+e.yxy).x +	e.xxx*DE(p+e.xxx).x));
}

/***********************************************/
vec3 tex3D(vec3 pos, vec3 nor, sampler2D s) {
	return texture( s, pos.yz).xyz*abs(nor.x)+
	       texture( s, pos.xz).xyz*abs(nor.y)+
	       texture( s, pos.xy).xyz*abs(nor.z);
}

/***********************************************/
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
    p.x *= iResolution.x/iResolution.y;	
	vec3 ta = vec3(0.0, 0.0, 0.0);
	vec3 ro =vec3(0.0, 2.0, -3.0);
	vec3 lig=normalize(vec3(-1.0, 2.0, 0.0));

    ro.xz=rot2(ro.xz, iTime*0.2);

	vec3 cf = normalize( ta - ro );
    vec3 cr = normalize( cross(cf,vec3(0.0,1.0,0.0) ) );
    vec3 cu = normalize( cross(cr,cf));
	vec3 rd = normalize( p.x*cr + p.y*cu + 2.5*cf );

	vec3 col=vec3(0.5);
	/* march */
	vec2 r=vec2(0.0);	
	float f=0.0;
	vec3 ww;
	for(int i=0; i<90; i++) {
		ww=ro+rd*f;
		r=DE(ww);		
		if( r.x<0.0 ) break;
		f+=r.x*0.5;
	}
	/* colors */
	if( f<30.0 ) {
		vec3 nor=normal(ww);

		if (r.y==1.0) col=vec3(1.0,0.6,0.3)*tex3D(ww,nor,iChannel0);
		if (r.y==2.0) col=vec3(0.2);
		if (r.y==3.0) col=vec3(1.0)*tex3D(ww*0.3,nor,iChannel0);;

			float amb =0.2;//*ww.y;
			float dif =0.7*clamp(dot(lig, nor), 0.0,1.0);
			float bac =0.2*clamp(dot(nor,-lig), 0.0,1.0);	

		col*=(amb+dif+bac);
	}

	fragColor = vec4( col, 1.0 );
}
