void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 p = fragCoord.xy / iResolution.xy;
	float aspectCorrection = (iResolution.x/iResolution.y);
	vec2 coordinate_entered = 2.0 * p - 1.0;
	vec2 coord = vec2(aspectCorrection,1.0) *coordinate_entered;
    coord *= .6+sin(iTime+length(coord)*sin(length(coord)+iTime))/2.65;
    coord *= rotate(iTime);
    coord *= vec2(1.)+0.1*vec2(sin(iTime+coord.x*10.), cos(sin(iTime)*11.+coord.y));
	float vignette = 2.0 / max(0.5 + 12.0*dot(coord,coord),1.);
    float time = iTime * 0.9;
    float dist = 0.0;
    vec2 goto_pos = vec2(0.5) - vec2(rand( vec2(0.0, 0.0) ), rand( vec2( 0.0 ) ) );
    float millisecs = time - floor(time);

    float Tfast = mod(time,1.0+sin(iTime/16.)/5.);
    float boing = 0.5-0.5*cos(Tfast*10.0)/(1.0+Tfast*Tfast*20.0);
    for(float k=1.0; k<10.0; k++){
        vec2 pos = vec2(0.0);
        for(float i=1.0; i<12.0; i++){
            float j = floor(time) + i ;
            float new_j = floor(time+1.0) + i;
            vec2 cur_pos = pos + 0.7 * (vec2(0.5) - vec2(rand( vec2(k, j) ), rand( vec2( j * k ) ) ) );
            vec2 new_pos = pos + 0.7 * (vec2(0.5) - vec2(rand( vec2(k, new_j) ), rand( vec2( new_j * k ) ) ) );
            dist += sdCapsule( coord, pos, cur_pos * (1.0 - boing) + new_pos * boing, 0.01 - i * 0.0008 );
            pos = cur_pos * (1.0 - boing) + new_pos * boing;
        }
    }
    for(float i=0.0; i<20.0; i++){
        vec2 circle_position = mod(iTime+i,5.0)*vec2(0.5-rand(vec2(i)),0.5-rand(vec2(i*i)));
        dist += 0.8 / max(0.2 + 100.0*dot(coord-circle_position,coord-circle_position),0.8);
    }
    vec3 COLOR = +vec3(0.2,0.9,0.8)-vec3(min(max(1.0/dist,0.0),1.0))+vignette-0.025*rand(time*coord*coord);
	fragColor = vec4(COLOR,1.0);
}
