void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0*(fragCoord.xy/iResolution.xy) - 1.0;
    float i,j,k = 0.0;
    float t = iTime;
    float vol = texture(iChannel0, vec2(abs(sin(t) * 100.0), 1.0)).x;

    i += 1.0 - abs(
        (2.4 * uv.x +
         sin(0.1 * (t + (vol*10.0)) + (uv.y * (1.0 + (vol*7.0))))));// +
//         sin(t + (uv.y * 13.0)))) * (1.0 + (vol * 50.0)));

//    j += abs(sin(t * 10.0 * uv.x * uv.y)) * 10.0;

//    k += sin(uv.x + t);

    k += uv.x + vol;

    fragColor = vec4(i,j+k,k,1.0);
}
