void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
	float pos = texture( iChannel0, vec2( uv[0], 0.5 ) ).x/5.0;
    float thickness =0.02;
	fragColor.r = 1.0;
    if ((uv[1] - pos) < thickness) {
        fragColor.r = (uv[1] - pos)*10.0;
    }
    if ((1.0 - uv[1] - pos) < thickness) {
        fragColor.r = 0.0;
    }
    fragColor.g = 0.0;
    fragColor.b = 0.0;
    fragColor.a = 1.0;
}
