float n(vec2 p,int j)
{
    p=abs(fract(p)-.5);
    for(int i=0;i<20;++i)if(i<j*4)p+=p.yx*vec2(1,-1)*1.73,p.x=abs(p.x)-1.;
   	p.y*=.01;
    return pow(length(p),.25)/pow(2.,float(j));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 t=fragCoord.xy/iResolution.y*1.5;
    fragColor.rgb=vec3(0,.075,.3)+vec3(.54,.26,.09)*(4.+n(t*4.,5))*
        min(min(n(t,5),n(t*.5,5)*1.5),n(t*.25,2)*2.);
}
