#define time iTime*0.1
#define s sin(time)
#define c cos(time)
#define m2D mat2(c,s,-s,c)

vec3 FractalCol(vec2 uv ,vec3 bgCol){
	vec2 z = uv;
    vec3 col = vec3(0.0);
    for(int i=0;i<12;i++){
    	float x = (z.x*z.x - z.y) + uv.x;
    	float y = (z.x*z.y + z.x) + uv.y;
        if(x*x + y*y > 4.0){break;}
        uv*=m2D;
        z*=m2D;
    	z = smoothstep(-0.5,0.5,vec2(x,y))-0.5;
        col += length(z*z*z)*length(z*z*z);
    }
    return col*bgCol*length(z*z*z*z)*12.0;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy-0.5;
    uv.x*=iResolution.x/iResolution.y;
	uv*= mat2(0.0,1.0,-1.0,0.0);
    vec2 auv=abs(uv*2.0);
    vec3 bg = mix(texture(iChannel0,m2D*auv.yx).xyz,
                  texture(iChannel1,m2D*auv.yx).xyz,sin(iTime)*0.5+0.5);
    bg = (bg);
    vec3 fc = FractalCol(auv,bg)*4.0-vec3(length(uv*vec2(0.9,0.6))*2.0);
	fragColor = vec4(fc,1.0);
}
