float f = 0.15, r = 0.4, h=0.5;
void mainImage( out vec4 fragColor , in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.y;
    vec2 ak = abs(fragCoord.xy / iResolution.xy-0.5);
    float g = 1.0/1.618033988749;
    float t = iTime * 0.7 + fract(sin(uv.x+1.0))*2.0;
    float e = 1.0 + sin(uv.x*3.0)*2.6;
    float k =  cos(t - e) + 2.0;
    vec4 tmp  = vec4( abs( sin(t + cos(0.5 * t + (uv.x+t*0.001) * k) ) ));
    float m = sin(iTime*0.37);
    if(m > .0){
        if(uv.y > h){
          tmp = vec4( abs( cos(t + sin(0.5 * t + (uv.x+t*0.001) * k) ) ));
        }
      }   
    fragColor  = tmp * smoothstep(ak.x,ak.x+f,r) * smoothstep(ak.y,ak.y+f,r); 
    if(m < .0){
      if(uv.x>1.0) fragColor  = 1.0 - fragColor ;
    }
}
