#define CENTER (iResolution.xy / 2.0)
#define PI 3.1415

//Normalized sinc function
float sinc(float x) {
    return sin(PI * x) / (PI * x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy - CENTER) / iResolution.y;
    float r = length(uv);
    float brightness = 2.0 * sinc(50.0 * r - iTime);
    fragColor = brightness * vec4(0.5, 1.0, 0.0, 1.0);
}
