void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 t = texture(iChannel0, .102 * uv + iTime * -.0051 * vec2(1., 0.));
    vec4 t0 = texture(iChannel0, .11 * -t.rb + iTime * -.02 * vec2(-0.5, 1.));
    vec4 t1 = texture(iChannel1, t0.rg -  iTime * -.3002);
	fragColor = vec4(t1 - t0 * .5) * 2.;

}
