void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
        vec4 R = texture(iChannel1,(fragCoord.xy+vec2(1,0))/iResolution.xy);
        vec4 L = texture(iChannel1,(fragCoord.xy-vec2(1,0))/iResolution.xy);
        vec4 U = texture(iChannel1,(fragCoord.xy+vec2(0,1))/iResolution.xy);
        vec4 D = texture(iChannel1,(fragCoord.xy-vec2(0,1))/iResolution.xy);
        vec4 T = texture(iChannel1,(fragCoord.xy)/iResolution.xy);
        float s = (R.y+L.y+U.x+D.x)/2.;
        float d = (R.x+L.x-U.y-D.y)/2.;
        vec2 Tp = vec2(s+d,s-d)/2.;
        T.xy = mix(T.xy,Tp,.5);
        fragColor = T;

}
