void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    // Vignette
    /*
    float boost = 1.1; 
    float reduction = 2.0; 
    vec3 col = texture(iChannel0, uv).rgb;
    float vignette = distance( iResolution.xy * 0.5, fragCoord.xy ) / iResolution.x;
    vignette = boost - vignette * reduction;
    col *= vignette;
    */

    // Sepia
    float amount = 1.0;
    vec3 col = texture(iChannel0, uv).rgb;
	float r = col.r;
	float g = col.g;
	float b = col.b;
    col.r = min(1.0, (r * (1.0 - (0.607 * amount))) + (g * (0.769 * amount)) + (b * (0.189 * amount)));
	col.g = min(1.0, (r * 0.349 * amount) + (g * (1.0 - (0.314 * amount))) + (b * 0.168 * amount));
	col.b = min(1.0, (r * 0.272 * amount) + (g * 0.534 * amount) + (b * (1.0 - (0.869 * amount))));

	fragColor = vec4(col, 1.0);
}
