void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float x = fragCoord.x;
    float y = fragCoord.y;

    vec4 color = vec4(1.0,1.0,1.0,1.0);

    float desiredWaveWidth = 128.0;
    float wavesOnScreen = iResolution.x / desiredWaveWidth; // 5 = 640 / 128

    float lineWidth = 10.0;

    float sinWaveHeight = 130.0;    
    float sinWaveWidth = iResolution.x / (3.141592 * wavesOnScreen); // 40,74367 = 640 / 15.70796

    float xInWidth = x / sinWaveWidth;

    float sinx = sin(xInWidth);

    float yInHeight = sinWaveHeight * sinx;
    float centerScreenOffset = (iResolution.y / 2.0) - sinWaveHeight / 2.0 + 50.0;

    float siny = yInHeight + centerScreenOffset;

    float distance = abs(siny - y);

    if(distance < lineWidth){

        color.g = 0.2;
        color.b = 0.2;
    }

    fragColor = color;
}
