void mainImage( out vec4 O, in vec2 fc ){
    vec2 uv = fc/iResolution.xy + vec2(1.5, -.5);    
    float t = 12.3456 + iTime,
    w = cos(uv.x * 5. + t * 3.7);

    vec4 final = vec4(0);

    float   ff = floor(uv.y * 7.5 + w) * 10.,
    gg = floor((uv.x + 1.5) * 7.5 + w) * 10.,
    bb = sin(uv.y * 1.5)*10.0,
    zz = 37.37 * uv.x + t * ff;
    zz += iMouse.z > 0.0 ? 0.0 : gg ;
    bb += uv.y * 10.;
    float kk = sin(zz + uv.y - bb ) * 10. ;
     final = vec4(kk);


   float  f = sin(3.1416*(uv.y * 7.5 + w)) * 2.,
    g = floor((uv.x + 1.5) * 7.5 + w) * 10.,
    b = sin(uv.y * 1.5)*10.0,
    z = 37.37 * uv.x;
    z += iMouse.z > 0.0 ? 0.0 : g ;
    b += uv.y * 10.;
    float  k = sin(z + uv.y - b )*f * 10. ;

    if(uv.y<0.0){
        final = vec4( smoothstep(1.5,0.,k/fwidth(k)) );
    }

    O = final;    
}
