#define PI 3.1415926536
#define FREQUENCY 10.0
#define PITCH 440.0

vec2 mainSound( in int samp, float time ) {
    float s = cos( 2.0 * PI * PITCH * time );
    s = 0.05 * s;    //lower volume
    return vec2(s);
}
