void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 c = fragCoord.xy / iResolution.xy - vec2(0.5);

    if(iMouse.x == 0.0 && iMouse.y == 0.0)
    {
        c += vec2(sin(iTime/10.0), cos(iTime/10.0)) * 1.5;
    }
    else
    {
        c += (iMouse.xy / iResolution.xy - vec2(0.5)) * 7.0;
    }

    c *= 0.5;

    vec2 z = vec2(0.0);
    vec2 zn;
    vec2 zmin = vec2(1000000.0);

    for(int i = 0; i < 64; i++)
    {
        zn.x = z.x * z.x - z.y * z.y + c.x;
        zn.y = 2.0 * z.x * z.y + c.y;
        z = zn;
        zmin = min(zmin, abs(zn));
    }

    float near = min(zmin.x, zmin.y) * 2.0;

	fragColor = vec4(
        sin(near - 1.0),
        sin(near + 0.2),
        sin(near + 0.8),
        1.0);
}
