#define t iTime
#define r iResolution

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord / r.xy - .5) * 2.;
    uv.x *= r.x / r.y;

    vec3 c;
    float j = 0.;
    for (int i = 0; i < 3; i++) {
     	vec2 p = uv * 3.5;
        j += sin(float(i) + p.x * p.y + t * 3.);
        c[i] = j;
    }

    fragColor = vec4(c, 1.0);
}
