#define NSPC 256
#define pi2 6.283185307179586476925286766559

float kick(float tb, float time)
{
	tb = fract(tb / 4.0) * 0.5;
	float aa = 5.0;
	tb = sqrt(tb * aa) / aa;

	float amp = exp(max(tb - 0.15, 0.0) * -10.0);
	float v = sin(tb * 100.0 * pi2) * amp;
	return v;
}

vec2 mainSound( in int samp, float time )
{
    vec2 mx = vec2(0.0);
    float tb = time * 8.0;
    mx += kick(tb, time);
    return mx;
}
