#define PI 3.1415
// fragColor is RGBA output fragment per pixel
// fragCoord is 2D xy input coordinate of each pixel
void mainImage(out vec4 fragColor,
               in vec2 fragCoord)
{
    float time = iTime;

    vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 p = mod(uv, vec2(1.0) / iChannelResolution[0].xy);
    float r = iSampleRate * .05;
    mat2 m = mat2(1, cos(uv.x * r + time), cos(uv.y * r + time), 1);
    vec4 n = texture(iChannel0, uv * m - p);
    vec4 c = texture(iChannel1, uv);
    float d = length(fragCoord.xy - iResolution.xy * .5) * iDate.x * .0001;

    int timeMod = int(mod(iTime, 4.0));
    if(timeMod == 0)
    {
        c.rgb = sin(sin(mod(c.rgb, n.rgb) * PI * 2.0 + d) * PI * 2.0 + time + 2.0 * d);
    }
    else if(timeMod == 1)
    {
        c.rgb = cos(sin(mod(c.rgb, n.rgb) * PI * 2.0 + d) * PI * 2.0 + time + 2.0 * d);
    }
    else if(timeMod == 2)
    {
        c.rgb = sin(cos(mod(c.rgb, n.rgb) * PI * 2.0 + d) * PI * 2.0 + time + 2.0 * d);
    }
    else if(timeMod == 3)
    {
        c.rgb = cos(cos(mod(c.rgb, n.rgb) * PI * 2.0 + d) * PI * 2.0 + time + 2.0 * d);
    }

    vec4 chan2 = texture(iChannel2, uv * m - p);
    vec4 chan1 = texture(iChannel1, uv * m - p);
    c.rgb = mix(c.rgb, chan2.rgb, chan1.rgb);

    fragColor = c;
}
