mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv += vec2(0.2, 0.1);
    uv *= 80.;

    // [ 0, 1 ]
    float spirally = cos(iTime / 10.);

    float normy = 1. + pow(iMouse.x / iResolution.x, 4.) * 50.;
    float flava = 20. * iMouse.y / iResolution.y;

    uv += (rotate2d(cos(iTime / 10.) * 2.6) * uv);

    uv += (rotate2d(length(uv) * spirally) * uv * -2.) * 0.5;

    uv += (iMouse.xy / iResolution.xy) * 50.;

    uv += cos(uv) * 2.;

    uv = mix(uv, uv * 1. / (1. + length(uv.yx)), 0.8);
    uv += sin(uv.yx * 1.) * 0.5;

    uv *= 0.1 / spirally;

    uv *= normy / (normy + pow(length(uv.yx), 2.));

    uv += sin(uv.yx * flava) * 0.25;

    uv += cos(uv + cos(uv*12.) * 0.25) * 0.5;
    uv += (rotate2d(iTime + length(uv) / 4.) * (uv.yx * -4.)) * 0.25;

    uv += cos(uv + cos(uv*12.) * 0.25) * 0.5;
    uv += (rotate2d(iTime + length(uv) / 8.) * (uv.yx * -8.)) * 0.02;

    // Time varying pixel color
    vec3 col = (cos(uv)).xyy; // * vec3(log(1. + length(uv)) * 1.);

    // vec3 col = vec3(log(length(uv)) / 2.);

    // Output to screen
    fragColor = vec4(col,1.0);
}
