// Fairly accurate UK flag with smooth edges (255 chars)
void mainImage(out vec4 O,vec2 U){
    vec2 r = 1. / iResolution.xy,
         u = U * r - .5;
    O = abs(u).xyxx;
    O.z -= O.y;
    O.w = min(O.x * 2., O.y);
   	O = smoothstep(0., r.y, vec4(.1 - O.w,
                                 O.w * .6 - .1,
                                 abs(O.z) * .5 - .06,
                                 .03 - abs(O.z * sign(u.x * u.y) / 1.2 + .03)));
    O = 1. - vec4(.2,.9,.8,0) * (O.y * O.w + O.x)
           - vec4( 1,.9,.5,0) * O.y * O.z;}

