void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy;
	float time = iTime;
	const float angleGoTo = 10.0 * 3.14;
	bool isOnGraph = false;
	for (float angle = 0.0; angle < angleGoTo; angle += angleGoTo / 1000.0)
	{
		if (angle >= (angleGoTo / 1000.0) * time * 30.0)
			break;
		vec2 pos = vec2(angle, 1.5 * sin(angle) + 2.0);
		pos *= 100.0;
		if (length(uv - pos) < 5.0)
		{
			isOnGraph = true;
			break;
		}
	}
	if (isOnGraph)
		fragColor = vec4(1.0, 1.0, 1.0, 1.0);
	else
		fragColor = vec4(0.0, 0.0, 0.0, 1.0);
}
