float s(float t) { return sin(t); }
float c(float t) { return cos(t); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float aspect = iResolution.y / iResolution.x;
    uv.y *= iResolution.x / iResolution.y;
	vec2 nuv = uv * 2.0 - 1.0;
    float t = iTime;
    float bpm = 1.1 * 3.1415926535 * 2.0;

    float l = clamp(length(nuv), 0.0, 0.75);
    float lm = texture(iChannel0, vec2(l, 0.25)).x;
    float bass = texture(iChannel0, vec2(0.05, 0.25)).x;

    float c0 = l * lm * 2.0;
	float c1 = c(t * bpm) * pow(lm, 4.0) * 4.0;
    float c2 = s(t * 0.1) * pow(lm, 6.0) * 3.0;

    float cb0 = pow(uv.y, 0.3) * 1.0;
    float cb1 = pow(uv.y - 0.7, 2.1);

    float n0 = texture(iChannel1, uv).x;
    float s0 = pow(sin(nuv.x / aspect * 100.0 + s(t * bpm) + c(t * bpm * 2.0)) * n0 * bass * 0.1, 6.0) * uv.y;
    float s1 = pow(sin(nuv.y * 80.0 + s(t * bpm * 3.0) * c(t * bpm * 1.0)) * n0 * pow(bass * 1.7, 1.5), 8.0) * uv.y;

    float red = s0 * 3.0 + s1 * 0.3 * cb1 * uv.y;
    red += c0 * cb1 * 1.0;
    float green = c1;
    float blue = c2 + cb0;

    fragColor.xyz = vec3(
        red,
        green + green * texture(iChannel0, uv).x,
        blue
    );


    // BPM: 132
    // key: Em
}
