// Copacabana in a Tweet



void mainImage(out vec4 C,vec2 u){
    vec2 U = 40.*u/iResolution.xy;
    U.y -= cos(U.x) + iTime ;
    C +=  mix(cos(U.x) * sin(U.y), .3, cos(U.y) * .35) * 20.;}




