#define SWIRL
#define ROTATE
//comment out both the defines and stare at the middle to really mess up your vision
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//setup the coordinates and square things up
	vec2 uv = fragCoord.xy - iResolution.xy*.5;
	uv /= iResolution.x;
	//swirl the origin around the center
	#ifdef SWIRL
		uv.x -= (sin(iTime * 2.) * 0.1);
		uv.y -= (cos(iTime * 2.) * 0.1);
	#endif
	//rotate the screen //https://www.shadertoy.com/view/Msf3Wr
	#ifdef ROTATE
		vec2 tuv = uv;
		float rot=sin(iTime)*0.7;		
		uv.x = tuv.x*cos(rot)-tuv.y*sin(rot);
		uv.y = tuv.x*sin(rot)+tuv.y*cos(rot);
	#endif
	//draw the stripes //23 and 7 are lucky numbers //the +length(uv) makes the stripes look nicer
	float b = length(cos(23.*sqrt(23.*uv.x*uv.y) + iTime * 23. )) + length(uv); //inward stripes
	if(cos(7.*sqrt(23.*uv.x*uv.y) - iTime * 23.) > 0.7) b = 0.7; // outward stripes
	//makes the colours change over time
	float col = smoothstep(b, 1.0-b, sin(iTime * 0.23));
	fragColor = vec4(b,-col*0.7,col, 1.0);
}
