void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	vec2 coord = iMouse.zw;
	float tmpl = clamp( 1.0 - length(coord - fragCoord.xy) / 100.0, 0.0, 1.0);


	vec3 color = vec3( 0, tmpl, 0 ) * sin(iTime * 5.0) * 10.0;

	fragColor = mix(vec4( color, 1.0 ), vec4(uv, 0.0, 0.0), 0.5);
}
