{
	vec2 uv = fragCoord.xy / iResolution.xy;
	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    float x = uv.x;
    float y= uv.y + cos(x*64.0)/8.0;
    x += cos(uv.y * 64.0)/32.0;
    vec4 c = texture(iChannel0, vec2(x,y));
    float n = 1.0 - (c.r + c.g + c.b ) / 3.0;
    fragColor = vec4(1.0-c.r, 1.0-c.g, c.b, 0);
