void mainImage( out vec4 O, in vec2 w )
{
    O-=O;
    vec2 R = iResolution.xy;
	vec3 p = vec3((w+w-R.xy)/R.y, .5), z=normalize(p);
    for (float i = 1.; i > 0.; i-=.01)
    {
        float T = iTime,
              x = length(cos(p)-cos(p.z-T)*sin(p.y-T))-1.;     
        O=i*i-O;
        if (x < .005) break;
        p += z * i;
    }
}
