float C,S;
#define rot(a) mat2(C=cos(a),S=sin(a),-S,C)

float map(vec3 p) {
    p.xy*=rot(iTime*.3);
    p.yz*=rot(iTime*.5);
    p.xz*=rot(iTime*.7);
    vec2 q=vec2(length(p.xz)-1.,p.y);
  	return length(q)-.2;
}

vec3 normal(vec3 p) {
    vec2 e=vec2(.001,0.);
    return normalize(vec3(
        map(p+e.xyy)-map(p-e.xyy),
        map(p+e.yxy)-map(p-e.yxy),
        map(p+e.yyx)-map(p-e.yyx)
    ));
}

float clr(vec2 uv) {
    vec3 ro=vec3(uv,-2.),rd=normalize(vec3(uv,1.)),mp=ro;
    float f;for(f=0.;f<30.;++f){
        float md=map(mp);
        if(md<.001)break;
        mp+=rd*md;
    }
    if(length(mp)>1e2)return 1.;
    vec3 n=normal(mp);
    return length(cross(n,rd));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv-=.5;
    uv.x*=iResolution.x/iResolution.y;
    vec2 st=uv*40.,fst=fract(st),ist=floor(st);
    vec2 wp=ist+step(.5,fst),bp=ist+vec2(.5);
    float wl=length(st-wp),bl=length(st-bp);
    float c=step(clr(uv)-.5,(bl/(bl+wl)-.5));
	fragColor = vec4(c);
}
