void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy-0.5;
    uv.x *= iResolution.x/iResolution.y;


    vec4 outColor = vec4(0.);

    vec2 polarUV = uv;
    polarUV.x = atan(polarUV.x, polarUV.y)+iTime/20.;
    polarUV.y = iTime/10.;
	outColor += texture(iChannel0, polarUV)/2.;

    vec2 radialUV = uv;
    radialUV.y = pow(length(radialUV),0.75+outColor.r/8.)-iTime/12.;
    radialUV.x = iTime/20.;
	outColor += texture(iChannel0, radialUV)/2.;

	fragColor = outColor;

}
