#define FOV_SCALE 2.

#define ITERATIONS 1024


#define SHADOW_ITERATIONS 86
#define EPSILON .002
#define NORMAL_EPSILON .02

#define VIEW_DISTANCE 128.

#define PATH_TRACE

//#define REFLECTIONS

#define pi 3.141592
#define fogColor vec3(.04,.07,.1)


