//distance functions from iq's site
float sdTorus( vec3 p, vec2 t ) {
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}
float udBox( vec3 p, vec3 b )
{
  return length(max(abs(p)-b,0.0));
}
float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) +
         length(max(d,0.0));
}
float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r;
}
float sdTriPrism( vec3 p, vec2 h )
{
    vec3 q = abs(p);
    return max(q.z-h.y,max(q.x*0.866025+p.y*.5,-p.y)-h.x*0.5);
}
float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}



vec2 rot(in vec2 v, in float ang) {
    float si = sin(ang);
    float co = cos(ang);
    return v*mat2(si,co,-co,si);
}


float ground(in vec3 rp) {
    return -rp.y+2.;
}
float sphere1(in vec3 rp) {
   	return length(rp-vec3(5.,0.,0.))-2.;
}
float sphere2(in vec3 rp) {
   	return length(rp-vec3(0.,-5.5,0.))-2.;
}
float sphere3(in vec3 rp) {
   	return length(rp-vec3(-5.,0.,0.))-2.;
}
float box(in vec3 rp) {
    return max( -sdBox(rp-vec3(0.,0.,1.), vec3(8.,9,9.)),
                sdBox(rp,vec3(9.,10.,9.)));
}
float dlight(in vec3 rp) {
    return udBox(rp-vec3(0.,-3.,0.),vec3(1.,.1,1.));
    //return sdTorus(rp-vec3(0.,-5.,0.), vec2(5.,.3));
}

float df(in vec3 rp) {
	return min(ground(rp), min(sphere1(rp), min(sphere2(rp), min(sphere3(rp), min(box(rp), dlight(rp))))));
}
float df_hq(in vec3 rp) {
	return df(rp);
}



const vec3 ne = vec3(NORMAL_EPSILON,0.,0.);
vec3 normal2(in vec3 rp) {
    return normalize(vec3(df(rp+ne)-df(rp-ne),
                          df(rp+ne.yxz)-df(rp-ne.yxz),
                          df(rp+ne.yzx)-df(rp-ne.yzx)));
}


vec3 normal(in vec3 rp) {
    return normalize(vec3(df_hq(rp+ne)-df_hq(rp-ne),
                          df_hq(rp+ne.yxz)-df_hq(rp-ne.yxz),
                          df_hq(rp+ne.yzx)-df_hq(rp-ne.yzx)));
}

material mat(vec3 rp) {
    material m;

    if (ground(rp) < EPSILON) {
        m = material(vec3(.8),
                     vec3(.8),
                     vec3(0.),
                     0.,
                     0.9,
                    1.,
                    1.);

    }
    if (sphere1(rp) < EPSILON) {
        m = material(vec3(.8),
                     vec3(1.),
                     vec3(0.),
                     1.,
                     0.02,
                    1.,
                    1.);
    }
    if (sphere2(rp) < EPSILON) {
        vec3 c = abs(normalize(rp-vec3(0.,-5.5,0.)))*.5+.5;
        m = material(c, c,
                     vec3(0.),
                     .0,
                     .06,
                    .02,
                    1.14);
    }
    if (sphere3(rp) < EPSILON) {
        m = material(vec3(.8,.8,0.), vec3(.8),
                     vec3(0.),
                     0.,
                     1.,
                    1.,
                    1.);
    }
    if (box(rp) < EPSILON) {
                m = material(vec3(.9),
                     vec3(.95),
                     vec3(0.),
                     0.,
                     1.,
                     1.,
                     1.);
    }
    if (dlight(rp) < EPSILON) {
                m = material(vec3(1.),
                     vec3(1.),
                     abs(normalize(rp-vec3(0.,-3.,0.))).yzx*20.,
                     0.,
                     1.,
                     1.,
                     1.);
    }

    return m;
}



float trace(in vec3 rp, inout vec3 rd, float maxDist) {

    float d,s = 0.;
    for (int i = 0; i < ITERATIONS; i++) {
        d = df(rp+rd*s);
        if (d < EPSILON || s > maxDist) break;
        s += d;
    }

    if (d < EPSILON) return s;

    return -1.0;
}
vec3 background(in vec3 rd) {
  return vec3(0.);
}

vec3 randomHemiRay(in vec3 d, in vec3 p, in float amount) {
    vec3 rand = normalize(cos(cos(p)*512.124+cos(p.yzx*16.234)*64.3249+cos(p.zxy*128.234)*12.4345));
    return rand*sign(dot(d,rand));
}
//ambient occlusion function is XT95's from https://www.shadertoy.com/view/4sdGWN
float ambientOcclusion(in vec3 rp, in vec3 norm) {
    float sum = 0., s = 0.5;
    vec3 lastp;

    for (int i = 0; i < 32; i++) {
        vec3 p = rp+randomHemiRay(norm,lastp,.4)*s;
        sum += max(0., (s-df(p))/(s*s));//randomHemiRay(norm,rp,.5)*s);
        lastp = p;
        s += .2;
    }

    return clamp(1.-sum*.05, 0., 1.);
}

float softShadowTrace(in vec3 rp, in vec3 rd, in float maxDist, in float penumbraSize, in float penumbraIntensity) {
    vec3 p = rp;
    float sh = 0.;
    float d,s = 0.;
    for (int i = 0; i < SHADOW_ITERATIONS; i++) {
        d = df(rp+rd*s);
        sh += max(0., penumbraSize-d)*float(s>penumbraSize*4.);
        s += d;
        if (d < EPSILON || s > maxDist) break;
    }

    if (d < EPSILON) return 0.;

    return max(0.,1.-sh/penumbraIntensity);
}


vec3 locateSurface(in vec3 rp) {    
    vec3 sp = rp;
    for (int i = 0; i < 3; i++) {
        float sd = abs(df(rp));
        if (sd < EPSILON) return sp;
        sp += normal2(sp)*sd*.5;
    }
    return sp;
}
void lighting(in vec3 td, in vec3 sd, in vec3 norm, in vec3 reflDir, in material m, inout vec3 dif, inout vec3 spec) {
    float ao = ambientOcclusion(td,norm);
    dif = ambient*ao;
    spec = vec3(0.);

    #if nLights != 0
    initDynamicLights(td);
    for (int i = 0; i < nLights; i++) {
        vec3 lightVec = lights[i].position-td;
        float lightAtten = length(lightVec);
        lightVec = normalize(lightVec);
        float shadow = softShadowTrace(sd, lightVec, lightAtten, 0.3, 1.5);
        lightAtten = max(0., 1.-lightAtten/lights[i].size)*shadow;

    	dif += max(0., dot(lightVec,norm))*lights[i].color*lightAtten;
        spec += pow(max(0., dot(reflDir, lightVec)), 4.+(1.-m.roughness)*78.)*shadow*lightAtten*lights[i].color;
    }
	#endif

    //dif *= .5+ao*.5;
}

//copy of shade without reflection trace
vec3 shadeNoReflection(in vec3 rp, in vec3 rd, in vec3 norm, in material m) {
    vec3 sd = rp+normal2(rp)*EPSILON*10.;//locateSurface(rp)-rd*EPSILON*2.;

    //lighting
    vec3 reflDir = reflect(rd,norm);

    vec3 lightDif,lightSpec;
    lighting(rp,sd,norm,reflDir,m,lightDif,lightSpec);

    return (1.-m.metallic)*lightDif*m.diffuse +
        	(.5+m.metallic*.5)*lightSpec*m.specular +
        	m.emissive ;
}

vec3 shade(in vec3 rp, in vec3 rd, in vec3 norm, material m) {
    vec3 sd = rp+normal2(rp-rd*EPSILON*40.)*EPSILON*10.;//locateSurface(rp)-rd*EPSILON*2.;

    //lighting
    vec3 dlc = vec3(0.);


    vec3 slc = vec3(0.);
    vec3 reflDir = reflect(rd,norm);
    vec3 tReflDir = normalize(reflDir+cos(rp*245.245-rd*cos(rp*9954.345)*3532.423)*m.roughness*0.25);
    tReflDir *= sign(dot(tReflDir,reflDir));

    #ifdef REFLECTIONS
    float rtd = trace(sd,tReflDir,VIEW_DISTANCE);
    if (rtd < 0.) {
        slc = background(tReflDir);
    } else {
        vec3 rhp = sd+tReflDir*rtd;
        slc = shadeNoReflection(rhp,reflDir,normal(rhp),mat(rhp));
    }
    #endif

    vec3 lightDif,lightSpec;
    lighting(rp,sd,norm,reflDir,m,lightDif,lightSpec);
    dlc += lightDif;
    slc += lightSpec;

    float fres = 1.-max(0., dot(-rd,norm));

    return (1.-m.metallic)*dlc*m.diffuse +
        	slc*m.specular*((.5-m.metallic*.5)*fres+m.metallic*(.5+m.metallic*.5)) +
        	m.emissive ;
}

float hash3(in vec3 p) {
    return fract(abs(cos(p.x)*128.234)+abs(cos(p.y)*124.234)+abs(cos(p.z)*128.234)+
                 abs(cos(p.x*128.234)*18.234)+abs(cos(p.y*128.234)*18.234)+abs(cos(p.z*128.234)*18.234));
}

vec3 pathtrace(vec3 rp, vec3 rd) {
    material m;
    vec3 norm;
    vec3 sd = rp;//locateSurface(rp)-rd*EPSILON*2.;


    //lighting
    vec3 c = vec3(0.),
         nextCMult = vec3(1.);

    float ifrm = float(iFrame);
    #define rndifrm cos(vec3(ifrm*.0004923))*512.2479


    float d,s = 0.,lastD = 9999999.;
    for (int i = 0; i < ITERATIONS; i++) {
    	d = df(sd+rd*s);
        if (d < EPSILON && lastD > EPSILON) {

            c += nextCMult*m.emissive;

        	vec3 tp = sd+rd*s;
            m = mat(tp);
            tp -= rd*lastD;
            norm = normal(tp);
            if (hash3(tp+(rndifrm).yzx)-.0001 < max(m.opacity,m.metallic*.5)) {
                if (hash3(tp+rndifrm)-.0001 < m.opacity*.5*(1.-m.metallic)) {
                    nextCMult *= m.diffuse;
                    rd = randomHemiRay(norm,tp+rndifrm,1.);
                } else {
                    nextCMult *= m.specular;
                    rd = mix(reflect(rd,norm),
                             randomHemiRay(norm,tp+rndifrm,1.),
                              m.roughness);
                }
                d = EPSILON;
            } else {
                tp += rd*lastD;
                rd = mix(refract(rd,norm,1./m.refrIndex),
                             randomHemiRay(norm,tp+rndifrm,1.),
                              m.roughness);

                nextCMult *= m.specular*(1.+m.metallic);
            }
            sd = tp;
            s = 0.;

        } else {
            if (d > EPSILON && lastD < EPSILON) {

                sd += rd*s;
                norm = normal(sd);
                rd = mix(refract(rd,-norm,m.refrIndex),
                             randomHemiRay(norm,sd+rndifrm,1.),
                              m.roughness);

               s = 0.;

                nextCMult *= m.specular;
            }
        }
        lastD = d;
        s += max(EPSILON,abs(d));
        if (s > VIEW_DISTANCE) break;
     }
    if (d > EPSILON) {
        c += nextCMult*background(rd);
    }

    return c;
}

vec2 randUv(vec2 uv) {
    return cos(float(iFrame)*.10924+abs(cos(float(iFrame)*2.345+uv)*128.345)+abs(cos(uv.yx*32.345)*16.234));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy;
	vec2 uv = (fragCoord - R*.5)/R.x;

    initLights();
    initCamera();

    #ifndef PATH_TRACE
        vec3 rp = cameraLocation;
    vec3 rd = normalize(vec3(uv*vec2(1.,-1.)*FOV_SCALE,1.));

    rd.yz = rot(rd.yz,cameraRotation.y);
    rd.xz = rot(rd.xz,cameraRotation.x);

	float itd = trace(rp,rd,VIEW_DISTANCE);
    if (itd < 0.) {
        fragColor = vec4(background(rd),1.);
        return;
    }


    vec3 hp = rp+itd*rd;
    fragColor = vec4(mix(shade(hp,
                      rd,
                      normal(hp),
                      mat(hp)), fogColor, max(0.,itd/VIEW_DISTANCE)),1.);
	#else
    if (iFrame < 120 || mod(float(iFrame-120),float(nFramesPerShot)) > float(nFramesPerShot-10) ) {
        fragColor = vec4(0.);
        return;
    }
	initCamera();

    vec3 rp = cameraLocation;
    vec3 rd = normalize(vec3((uv+randUv(uv)*(1./iResolution.y)*.25)*vec2(1.,-1.)*FOV_SCALE,1.));

    rd.yz = rot(rd.yz,cameraRotation.y);
    rd.xz = rot(rd.xz,cameraRotation.x);

	fragColor = vec4(texture(iChannel0, fragCoord/R).xyz+pathtrace(rp,rd), 1.);
    #endif
}
