#define count 40.0
#define speed 0.06

bool IsBlack(in vec2 coor)
{
    vec2 iPos = ceil(coor * count);
    float sum = iPos.x + iPos.y;
    return mod(sum, 2.0) == 0.0;
}

vec2 Rotate(in vec2 coor, in float angle)
{
    float c = cos(angle);
    float s = sin(angle);

    return vec2(coor.x*c+coor.y*s, coor.y*c-coor.x*s);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 center = iResolution.xy / 2.0;
	vec2 uv = (fragCoord.xy-center) / min(iResolution.x, iResolution.y);

    float t = iTime * speed;

    float da = 6.28 * sin(t/3.0);
    vec2 dp = vec2(sin(t), cos(t));

    vec2 uv2 = Rotate(uv, da) + dp;

    if (IsBlack(uv) && IsBlack(uv2))
    {
        fragColor = vec4(0.0,0.0,0.0,1.0);
    }
    else if (IsBlack(uv) || IsBlack(uv2))
    {
        fragColor = vec4(0.5,0.5,0.5,1.0);
    }
    else
    {
        fragColor = vec4(1.0,1.0,1.0,1.0);
    }
}
