vec4 drawCircle(in vec2 inPos, in vec2 inUv, in vec4 outColour)
{
    float dist = distance(inUv, inPos);

    if (dist < 0.1)
    {
		return vec4(1.0, 0.0, 0.0, 1.0);
    }

    return vec4(0.0);
}

vec4 drawCurve(in vec2 inStart, in vec2 inEnd, in float inWidth, in vec2 inUv)
{
    //float xSin = sin(inUv.x);
	inStart.y -= .3*cos(2.*(inUv.x- 0.5))-0.3;

    if (inUv.x < inStart.x || inUv.x > inEnd.x) return vec4(0.0);

    if (inUv.y < (inStart.y - inWidth) || inUv.y > (inStart.y + inWidth)) return vec4(0.0);

    return vec4(1.0, 0.0, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 outColor = vec4(0.0);

    outColor += drawCircle(vec2(0.25, 0.75), uv, outColor);

    outColor += drawCircle(vec2(0.75,0.75), uv, outColor);

    outColor += drawCurve(vec2(0.15, 0.25), vec2(0.85, 0.25), 0.1, uv);

	fragColor = outColor;
}
