void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float r=0.05;
    float t=abs(uv.y-0.5-0.2*sin((uv.x)*30.0+10.0*sin(iTime)));

    if(t<r){
        fragColor=vec4(vec3(0.5+0.5*sin(iTime),0.5+0.5*sin(iTime*2.),0.5+0.5*sin(iTime*3.))*(r-t)/r,1);
    }else{
        fragColor=vec4(0,0,0,1);
    }
}
