#define PI 3.14159

// Starts the plot on a fixed absciss, comment to switch to start it on the circle. 
#define CurveFixedStart

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.y *= iResolution.y / iResolution.x;
    uv.y -= 0.25;
    uv *= 10.0;

    vec2 center = vec2(1.5, 0.0);
    float circle = abs(distance(uv, center) - 1.0);

    float dot = abs(distance(uv - center, vec2(cos(iTime), sin(iTime))));

    float dot2 = abs(distance(uv - vec2(0.25, 0.0), vec2(0.0, sin(iTime))));

    float wave = 1.0;
#ifdef CurveFixedStart
    float wavestart = 3.0;						// Start the curve on a fixed x
#else
    float wavestart = cos(iTime) + center.x;	// Start the curve on the dot
#endif

    if(uv.x > wavestart)
    {
        //if(mod((uv.x - iTime - wavestart), 0.2) < 0.15) // Dashed sin
        {
            uv.x -= iTime + wavestart + PI;
            wave = abs(sin(uv.x) - uv.y);
        }
    }

    fragColor = vec4((wave < 0.05 ? (0.05 - wave) * 10.0 : 0.0) + 
        	(circle < 0.05 ? (0.05 - circle) * 10.0 : 0.0) + 
        	(dot < 0.1 ? (0.1 - dot) * 10.0 : 0.0));
        	//(dot2 < 0.1 ? (0.1 - dot2) * 10.0 : 0.0);	// Tracks the value of sin(t)
}
