#define time iTime
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float tb = mod(time*4.0  , 32.0);
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    vec2 p = uv - vec2(sin(tb*.01)+.5, tan(tb*.01));
	vec3 col = mix(vec3(0.8, 0.2,0.1),vec3(1.0, 0.8,0.8),length(p));
    float r = .2;
    col *= smoothstep(r+.1, r+.01, length(p));
    fragColor = vec4(col, 1.0);
}

#define STEP 1.0595
float halfStepDown(float freq){

    return freq/STEP;
}
float halfStepUp(float freq){

	return freq*STEP;
}

