void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float l2 = length(uv-.5);
    float l = length(uv.y-.5);
    float a=.5+sin(iTime)/2.;
    float la = mix(l, l2, a);
    uv+=la*iDate.w;
    uv/=la;
	fragColor = vec4(texture(iChannel0,uv).rgb,1.0)*la;
}
