#define col(v) ( .5 + .5 * cos( 6.3 * (v)  + vec4(0,23,21,0)  ) )
#define rot(a)   mat2(cos(a),-sin(a),sin(a),cos(a))

#define T(U) ( texture(iChannel0,(U)/R) *.5 + .5 )

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy,
         u = U/R;
    if ( u.x < .5 ) 
        O = T(U/4.); 
    else {
        mat2 M = rot(1.3);
        U /= 16.;
        O = ( T(U) + T(U*M*2.)/2. + T(M*U*4.)/4. + T(U*M*M*8.)/8. ) / 2.;
    }
  //return;
    O = O.xxxx;
    if ( u.x > .5 || u.y < .5 ) O *= O * vec4(2,1.5,1,0);
    if ( u.y > .5 ) O = col(O.x);

}

#define rnd(v)   fract( 1234.5 * sin( v * vec2(73.1,5.73) ) )

#define T(U, d) ( texture( iChannel1, U/256. + d ) *2. - 1. )

float t = .1;

void mainImage( out vec4 O, vec2 U )
{
    if (iFrame==0) { O = T(U, 0.); return; }

    //O = 2.*texture(iChannel1,U+rnd(float(iTime))) - 1. ; return;

    O = (       texture(iChannel0,U/iResolution.xy) 
          + t * T( U, rnd(float(iTime)) )
        ) / sqrt(1. + t*t);                  // contrast preserving
}
