void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec4 color = texture(iChannel0, uv);
    color = vec4(pow(color.r, 2.2), pow(color.g, 2.2), pow(color.b, 2.2), 1.);
	float cM = 0.2126*color.r + 0.7152*color.g + 0.0722*color.b;
    cM /= 3.;
    vec4 BW = vec4(vec3(cM), 1.);
    float wt = abs(cos(iTime));

    vec4 fColor = mix(color,BW, wt);
    fColor = vec4(pow(fColor.r, 1./2.2), pow(fColor.g, 1./2.2), pow(fColor.b, 1./2.2), 1.);
    // Output to screen
    fragColor = fColor;
}
