void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = 3737.37+sin(iTime*0.137)*337.37;
    vec2 uv = fragCoord/iResolution.y;
    vec2 center = vec2(1);
    float d = distance(uv,iMouse.xy*0.01);
    float a = 0.1;
    float b = 0.1;
    t *= sin(fragCoord.y*a)*b;
    t *= cos(fragCoord.x*a)*b;
    float r = floor(mod(d - t, .5 )*3.0);
    fragColor = vec4( r);
}
