void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float miTime = iTime;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv.x *= iResolution.x / iResolution.y;

    uv -= 0.5;

    float tita = 3.14 * 0.5;
    tita *= sin(miTime);

    uv.x += 0.3 * sin( 1.0 * miTime);
    uv.y += 0.3 * cos( 2.0 * miTime + tita);

    // Output to screen
    float l = length(uv);

    l -= 0.1;

    l = 1.0 - l;

    l = pow(l,5.0);
    l = clamp(l,0.0,1.0);

    float l1 = l;

    // circulo 2

    //Reduce el tiempo (efecto "lag"/"drag")
    miTime -= 0.3;

    uv = fragCoord/iResolution.xy;

    uv.x *= iResolution.x / iResolution.y;

    uv -= 0.5;

    tita = 3.14 * 0.5;
    tita *= sin(miTime);

    uv.x += 0.3 * sin( 1.0 * miTime);
    uv.y += 0.3 * cos( 2.0 * miTime + tita);

    // Output to screen
    l = length(uv);

    l -= 0.05;

    l = 1.0 - l;

    l = pow(l,2.0);
    l = clamp(l,0.0,1.0);

    float l2 = l;


    fragColor = vec4(l1+l2);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime/*+uv.xyx*/+vec3(0,2,4));

    l = clamp(l1+l2,0.0,1.0);

    fragColor = vec4(l * col, 1.0);
}
