#define PI 3.14159

vec2 remap(vec2 coord) {
	return coord /min(iResolution.x,iResolution.y);
}

float lerp(float a, float b, float t) {
	return a + (a - b) * t;
}

float circle(vec2 uv, vec2 pos, float rad) {
	return 1.0 - smoothstep(rad,rad+0.005,length(uv-pos));
}

float ring(vec2 uv, vec2 pos, float innerRad, float outerRad) {
	return (1.0 - smoothstep(outerRad,outerRad+0.005,length(uv-pos))) * smoothstep(innerRad,innerRad+0.005,length(uv-pos));
}

vec3 hsv2rgb(vec3 c) {
  vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb);
  return c.z * mix( vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
 vec2 uv = remap(fragCoord.xy);

 vec2 t = remap(iMouse.xy);

 vec3 col = vec3(0.0);

 vec2 center = vec2(0.5*iResolution.x/iResolution.y,0.5);

 vec2 d = uv - center;
 float a = atan(d.x,d.y)*PI*0.05;

 col += ring(uv,center,0.47,0.5) * hsv2rgb(vec3(a,1.0,0.5));

 d = t - center;
 a = atan(d.x,d.y)*PI*0.05;

 col += circle(uv,center,0.2) * hsv2rgb(vec3(a,1.0,0.5));

 fragColor = vec4(col, 1.0);
}
