void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ((fragCoord.xy / iResolution.xy) - 0.5) * 2.0;

    float middlex = sin(iTime / 13.0);
    float middley = cos(iTime / 7.0);

	float r = sqrt(dot(uv, uv));
  	float v = uv.x*(middlex-sqrt(r*r))/(r*r+1.0);
  	float u = uv.y*(middley+sqrt(r*r))/(r*r+1.0);

    float slow_rot = iTime * 0.01;
    mat2 rot = mat2(cos(slow_rot) * middlex, sin(slow_rot) * middley, -cos(slow_rot) * middley, sin(slow_rot) * middlex);

	fragColor = texture(iChannel0, rot * vec2(u, v));
}
