// Logarithmic Spiral Transform - written 2015-12-02 by Jakob Thomsen
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
// Thanks to FabriceNeyret2 for optimizations
void mainImage( out vec4 O, vec2 I )
{
	vec2 R = iResolution.xy;
    I = (I+I-R)/R.y;
    //I = (I+I-(I=iResolution.xy))/I.y; // NOT COMPATIBLE

    I = vec2(0, iTime - log2(length(I))) + atan(I.y, I.x) / 6.283; // Thanks to FabriceNeyret2 for optimizations!
    I.x = ceil(I.y) - I.x;

    I.x *= 2.4; // approximation to golden angle pi*(3-sqrt(5))

    O = vec4(fract(I),0,1);
}
