void mainImage( out vec4 o, in vec2 f )
{
    float t = iTime;
    vec2 r = iResolution.xy;
	vec2 u = ((f + f - r)/r.y)* 2.;
    o = texture(iChannel0,u*u);
    float a = acos( dot( u, o.xy ) );
	float clr = length(u * tan(a) * cos(u.x) * sin(u.y));
  	o = vec4(vec3(.7, .15, .15) * clr, 1.);
}
