vec2 sFract(vec2 x) // sFract function by Shane
{
	x = fract(x);
	return min(x, x*(1.-x)*16.);
}

void mainImage(out vec4 C,vec2 U)
{
    vec2 R=iResolution.xy,
         p=abs(U+U-R)/R.y;   
    float b=length(p*=length(p)),
          a=length(--p),
          t=iTime;    
    p*=20.*(p+1.)*abs(sin((a-t)/2.)*sin(b+t/4.))+abs(sin(a*b+2.*t/3.));
    C=length(sFract(p))*(1.-b)*(.5+.5*sin(t/vec4(3,7,17,1)));
}
