void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 1.5 - 0.25;
    uv.y += 0.1 * sin(uv.x * 5.0 + 2.13 * iTime) * uv.x;
    uv.x += 0.03 * sin(1.14 * iTime) * uv.x;
    if(uv.y < 0. || uv.y > 1. ||uv.x < 0. ||uv.x > 1.) {
        fragColor = vec4(0.);
    } else if(uv.y > 4. / 11. && uv.y < 7. / 11. || uv.x > 5. / 18. && uv.x < 8. / 18.) { // Finnish flag ratios y:4:3:4  x:5:3:10
        fragColor = vec4(0., 53. / 255., 0.5 , 1.);
    } else {
        fragColor = vec4(1.);
    }

}
