#define PI 3.14159265359

float plot(vec2 st, float pct){
  return  smoothstep( pct+0.07, pct, st.y) - 
          smoothstep( pct, pct+0.07, st.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 st = fragCoord.xy/iResolution.xy;

    vec3 color = vec3(st.y * sin(iTime));

    float v = (cos((st.x * 10.0) + (iTime * (st.y/9000.0)) * 5.0)/ 5.0 )+ 0.5;

    float v2 = v + (cos((st.x * 800.0) + (iTime) * 5.0)/ 5.0 );

    float pct = plot(st ,v2);

    color = (pct)*color+pct*vec3(1.0,0.0,0.0);

	fragColor = vec4(color,1.0);
}
