precision mediump float;
#define PI 3.1415926535897932384626433832795

uniform vec2 u_k;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    float v = 0.0;
    vec2 c = fragCoord.xy / iResolution.xy*8.0;
    v += sin((c.x+iTime));
    v += sin((c.y+iTime));
    v += cos((c.x+c.y+iTime));
    c += vec2(sin(iTime/2.0), cos(iTime/3.0));
    v += cos(sqrt(c.x*c.x+c.y*c.y+1.0)+iTime);
    v = v/0.5;
    vec3 col = vec3(sin(v*PI), sin(v*PI+2.0*PI/3.0), sin(v*PI+4.0*PI/3.0));
    fragColor = vec4(col*.5 + .5, 1);
}
