float normsin(float a)
{
	return 0.5*(sin(a)+1.0);

}


float normcos(float a)
{
	return 0.5*(cos(a)+1.0);

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
//    vec2 uv = fragCoord/iResolution.xy-vec2(0.5,0.5);
    vec2 uv = fragCoord/iResolution.xy ;

    float r = length(uv) ;

    float nc = normcos(0.001*uv.x*iTime) ;
    float ns = normsin(0.01*uv.x*iTime) ;

    float f1 = 5.0 ;
//    float f2 = 4.4 * nc ;
//    float f3 = 7.0 * ns ; ;
    float f2 = 4.4  ;
    float f3 = 7.0  ;
    float p1 = 20.0 ;
    float p2 = 20.0 * nc;
    float p3 = 12.0*normcos(0.5*6.28*iTime)+12.0;

    // Time varying pixel color
    float w1 = normcos(5.0*6.28*iTime+uv.y+20.0*uv.y*6.28)  ;

    float w2 = normcos(f2*6.28*iTime+uv.x+p2*uv.x*6.28)  ;

    float w3 = normcos(f3*6.28*iTime-p3*r*6.28)  ;

    vec3 col = vec3(0, 0.5*w3+0.0*w2, 0) ;
    // Output to screen
    fragColor = vec4(col,1.0);
}
